
class DrawFigures {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		drawTopCon();
		drawBox();
		drawBottomCon();
	}
	
	public static void drawTopCon() {
		System.out.println("  /\\");
		System.out.println(" /  \\");
		System.out.println("/    \\");
		
	}
	
	public static void drawBox() {
		System.out.println("+----+");
		System.out.println("|    |");
		System.out.println("|    |");
		System.out.println("+----+");
	}
	
	public static void drawBottomCon() {
		System.out.println("\\    /");
		System.out.println(" \\  /");
		System.out.println("  \\/");
	}

}
